import React,{Component} from "react";
import {Switch , Button} from "react-bootstrap";
import Home from "./home";
class Status extends Component
{
        constructor(props)
        {
            super(props);

            this.state=
                {
                u_id:props.id,
                username:props.username,
                loggedIn:props.loggedIn,
                data:props.data,
                walletName:'',
                home:false
                }

                this.onValueChange = this.onValueChange.bind(this);
                this.handleSubmit = this.handleSubmit.bind(this);
                this.goBack=this.goBack.bind(this);
        }

        onValueChange(e)
        {
            this.setState({
                [e.target.name]: e.target.value

            });
        }

        handleSubmit(e)
        {
            e.preventDefault();
            const {walletName , amount} = this.state;
            const value= {walletName,amount}
            console.log(JSON.stringify(value));


            const apiUrl = '/credit/'+this.state.u_id;
            const requestOptions = {
                method: 'POST',
                headers: { 'Accept':'application/json','Content-Type': 'application/json' },
                body: JSON.stringify(value)
            };

            fetch(apiUrl,requestOptions)
                .then(response=> response.json)
                .then(data=>console.log(data))
                .then(alert("money added"))
                .then(this.setState({walletName:'',
                    amount:''}))
                .catch(err=>console.log(err))

            e.target.reset();
        }

        goBack(e)
        {
            e.preventDefault()
            this.setState({home:true})

        }
        render()
        {

        const data=this.state.data;
        let i=0;

        if(this.state.home)
        {
            console.log(this.props.username , this.props.loggedIn  ,this.props.data)
            return <Home username={this.props.username} loggedIn={this.props.loggedIn} data={this.props.data} />
        }

        return(

            <div className="page-holder bg-cover " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
                <header className="text-center text-grey py-5">
                    <div style={{backgroundColor:'white'}}>
                        <br/><h1 className="font-weight-bold">Block / Unblock Wallets</h1><br/>
                    </div>


                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                    <form >
                        <div className="form-group">
                            <h3>Choose Wallet to Block</h3>
                        <br/>

                            {data.map((d)=>{
                                return(

                                    <label>
                                        <span>{d.wallet_name}</span>
                                        <Switch className="react-switch" />
                                    </label>)
                                        {/*{ (!d.is_active) && <input type="checkbox" checked="checked" className="form-control custom-control-input" id="customSwitch1"/> }*/}
                                        {/*{ (!d.is_active) &&  <label className="custom-control-label" htmlFor="customSwitch1">{d.wallet_name}</label>}*/}


                                    })
                            }


                        </div>
                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        <br/>
                    </form>

                        <p className=" text-right">
                            <Button onClick={this.goBack}>Go Back</Button>
                        </p>
                    </div>
                </header>
            </div>
        )
    }
}

export default Status;
