import React, { Component } from "react";
import { useHistory } from "react-router-dom";
import {Message} from 'semantic-ui-react';
import {Redirect} from "react-router-dom";
import store from 'store';
import './body.css'


class Body extends Component
{
    constructor(props)
    {
        super(props)
        let loggedIn = false

        this.state = {
            name: '',
            password: '',
            loggedIn
        };


        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {

        event.preventDefault();

        const { name, password } = this.state;
        this.setState({ error: false });

        // if (!(name === 'roshni' && password === '123')) {
        //     return this.setState({ error: true });
        // }

        console.log("you're logged in!");
        store.set('loggedIn', true);

        const {history} =this.props;
        history.push('/home');


    }

    render()
    {
        const { error } = this.state;

        return(
            <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba;',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                <div className="container py-5 ">
                    <header className="text-center text-grey py-5 animate__animated animate__fadeInUpBig ">
                        <h4 className="display-4 font-weight-bold mb-4">Wallet As A Service</h4>
                    </header>

                    <div className="box">
                            <h2>Login</h2>
                        <form onSubmit={this.handleSubmit} error={error}>
                            {error && <Message
                                error={error}
                                content="That username/password is incorrect. Try again!"
                            />}
                            <div className="inputBox">
                                <input type="text" name="username" required />
                                    <label>Username</label>
                            </div>
                            <div className="inputBox">
                                <input type="password" name="password" required />
                                    <label>Password</label>
                            </div>
                            <button type="submit" className="btn btn-lg btn-primary ">Sign In</button>
                            <br/><br/>
                        </form>
                            <p className="text-right">
                                <p style={{color:'white'}}><span>New User?   </span>
                                <a href="/register"><button type="submit" className="btn btn-lg btn-primary ">Register</button></a>
                                </p>
                            </p>

                    </div>


                </div>

            </div>
        )
    }
}
export default Body;

