import React,{Component} from "react";
import {Button, Form} from "react-bootstrap";
import Switch from "react-switch";
import Home from "./home";
import {NotificationContainer, NotificationManager} from "react-notifications";
var st;

class Status extends Component
{
        constructor(props)
        {
            super(props);

            this.state=
                {
                u_id:this.props.location.id,
                username:this.props.location.name,
                loggedIn:this.props.location.loggedIn,
                data:this.props.location.data,
                walletName:'',
                home:false,
                blockedUsers:[],
                checkCount: 0,
                checkboxValid:false
                }

                this.onValueChange = this.onValueChange.bind(this);
                this.handleSubmit = this.handleSubmit.bind(this);
                //this.handleChange = this.handleChange.bind(this);
                this.goBack=this.goBack.bind(this);
        }

        onValueChange(event)
        {
            var joined
            if(event.target.checked)
            {
                this.setState((prevState) => ({checkCount: prevState.checkCount + 1}))
                joined = this.state.blockedUsers.concat(event.target.value);
                this.setState({blockedUsers: joined})
                st=this.state.blockedUsers
                console.log(st)
            }
            else {
                this.setState((prevState) => ({checkCount: prevState.checkCount - 1}))
                var index = this.state.blockedUsers.indexOf(event.target.value)
                joined = this.state.blockedUsers.splice(index,1);
                this.setState({blockedUsers: joined})
                st=this.state.blockedUsers
                console.log(st)
            }
        }

        handleSubmit(event)
        {
            event.preventDefault()
            var stt=this.state.blockedUsers

            console.log(stt,st)
            //var st = this.state.blockedUsers
            if (this.state.checkCount === 0) {
                // this.setState({checkboxValid: true})
                NotificationManager.error('', 'Nothing is Selected')
            }
            else {
                var final = {blockNames: (st)}
                console.log(JSON.stringify(final))

                const apiUrl = '/block/'+this.state.u_id;
                const requestOptions = {
                    method: 'POST',
                    headers: { 'Accept':'application/json','Content-Type': 'application/json' },
                    body: JSON.stringify(final)
                };

                fetch(apiUrl,requestOptions)
                    .then(res => res.json) // or res.json()
                    .then(res => console.log(res))
                    .catch(err=>console.log(err))

                NotificationManager.success('', 'Selected wallets Blocked!' )

            }

            //event.target.reset();

        }

        goBack(e)
        {
            this.props.history.push({
                pathname: '/home',
                data:[{ id:this.state.u_id , name :this.state.username }]// your data array of objects
            })
        }
        // handleChange(checked)
        // {
        // this.setState({ checked });
        // }

    render()
        {

        const data=this.state.data;
        let i=0;

        if(this.state.home)
        {
            console.log(this.props.username , this.props.loggedIn  ,this.props.data)
            return <Home username={this.props.username} loggedIn={this.props.loggedIn} data={this.props.data} />
        }

        return(

            <div className="page-holder bg-cover " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
                <div className="justify-content-center">
                <NotificationContainer />
                </div>
<br/><br/><br/><br/>
                <br/><br/>
                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <h3>Choose Wallet to Block</h3>
                        <br/>
                            <b><label> Wallets</label>
                                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span>Balance</span></b>
                            <table width="500"  cellSpacing="30" >

                            <Form.Group id="formGridCheckbox">
                            {data.map((d)=> {
                                return (
                                    <div key={i++}>
                                        {(d.is_active)
                                            ?
                                            <tr>
                                                <td width="150">
                                                    <Form.Check type="checkbox" label={d.wallet_name} key={i++}
                                                                value={d.wallet_name}
                                                                onClick={this.onValueChange}/>
                                                </td>
                                                <td width="150">{d.wallet_balance}</td>
                                            </tr>

                                            :

                                            <tr>
                                                <td width="150">
                                                    <Form.Check type="checkbox" defaultChecked label={d.wallet_name} key={i++}
                                                                value={d.wallet_name}
                                                                onClick={this.onValueChange}/>
                                                </td>
                                                <td width="150">{d.wallet_balance}</td>
                                            </tr>
                                        }
                                    </div>
                            )})
                            }
                            </Form.Group>
                            </table>


                        </div>
                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        <br/>
                    </form>

                        <p className=" text-right">
                            <Button onClick={this.goBack}>Go Back</Button>
                        </p>
                    </div>

            </div>
        )
    }
}

export default Status;
