import React ,{Component} from "react";

class DailyReport extends Component
{
    constructor(props) {
        super(props);

        this.state = {
            username: '',
            users: []
        };
        //
        // this.onValueChange = this.onValueChange.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);

    }


    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }

    render() {
        const {users}=this.state;
        return(
            <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
            backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
            <br/><br/>
            <div className="container py-5 ">
                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5" >
                    <form >
                        <h3>Daily Report</h3>
                        <hr/>
                        <div className="form-group">

                            <label>Choose Users</label>

                            <br/>
                            {users.map(item => (

                                <div className="radio" key={item.ID}>
                                    <label>
                                        <input
                                            type="checkbox"
                                            name={item.Username}
                                            value={item.Username}
                                            onChange={this.onValueChange}
                                        />
                                        {item.Username}
                                    </label>

                                </div>
                            ))}

                            <br/>
                            <button type="submit" className="btn btn-primary btn-block">Generate report</button>
                            <br/><hr/>
                            <p className=" text-right">
                                <a href="/">Go Back</a>
                            </p>
                        </div>
                    </form>
                </div>
                </div>
            </div>
    )
    }
}
export default DailyReport
