import React, {Component} from "react";
import {Form ,Modal ,Col ,Row ,Button} from 'react-bootstrap';

class AddModal extends Component{
    constructor(props) {
        super(props);

        this.state={
            wallet_name:'',
            u_id:props.id,
            username:props.username,
            loggedIn:props.loggedIn,
            data:props.data
        }

    }
    render() {
        return (
            <Modal
                {...this.props}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Add a Wallet
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <label>Wallet Name</label>
                            <input type="text" className="form-control" placeholder="Enter wallet name"
                                   required="required" id="walletname" name="walletname"/>
                        </div>


                        <button type="submit" className="btn btn-primary btn-block">Add Wallet</button>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.props.onHide}>Back</Button>
                </Modal.Footer>
            </Modal>
        )
    }

}



class DelModal extends Component{
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <Modal
                {...this.props}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Delete a Wallet
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form>
                        <div className="form-group">
                            <label>Choose Wallet to Delete</label>

                            <Form.Group id="formGridCheckbox">
                                <Form.Check type="checkbox" label="Wallet 1"/>

                                <Form.Check type="checkbox" label="Wallet 2"/>

                                <Form.Check type="checkbox" label="Wallet 3"/>
                            </Form.Group>


                        </div>
                        <button type="submit" className="btn btn-primary btn-block">Delete Wallet</button>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.props.onHide}>Back</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}




class Wallets extends Component
{
    constructor() {
        super();

        const [modalShow, setModalShow] = React.useState(false);
        const [modalShow2, setModalShow2] = React.useState(false);
    }

    render() {

        const [modalShow, setModalShow] = React.useState(false);
        const [modalShow2, setModalShow2] = React.useState(false);


        return (

            <div className="text-center page-holder bg-cover" style={{
                backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'
            }}>
                <br/><br/><br/><br/>
                <header>
                    <h1><b> Manage Wallets </b></h1>
                </header>

                <header className="text-center text-white py-5">
                    <div className="container">

                        <Button className="btn floatleft btn-xxl" onClick={() => setModalShow(true)} variant="secondary"
                                size="lg" style={{fontsize: '250px'}}>Add a Wallet</Button>

                        <Button className="btn floatright btn-xxl" onClick={() => setModalShow2(true)}
                                variant="secondary" size="lg">Delete a Wallet</Button>
                        <AddModal
                            show={modalShow}
                            onHide={() => setModalShow(false)}
                        />
                        <DelModal
                            show={modalShow2}
                            onHide={() => setModalShow2(false)}
                        />

                    </div>
                </header>
            </div>
        );
    }


}
export default Wallets;
