import React, { Component } from "react";

import {Form ,Col ,Row} from 'react-bootstrap';
class Credit extends Component
{
    constructor(props) {
        super(props);

        this.state={
            u_id:props.id,
            data:props.data,
            wallet:'',
            amount:''
        }

        this.onValueChange = this.onValueChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    onValueChange(e)
    {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        const {wallet , amount} = this.state;
        const value= {wallet,amount}
        console.log(JSON.stringify(value));

    }
    render() {

        const data=this.state.data;
        console.log(data)
        return (

            <div  className="page-holder bg-cover " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
                {console.log(data)}
                <br/><br/><br/><br/>
                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5" >

                    <form onSubmit={this.handleSubmit}>
                        <h3>Add Money</h3>
                        <hr/>
                        <div className="form-group">

                            <label>Choose Wallet</label>

                            <fieldset >
                                {data.map((d)=>{
                                    return(
                                    <div key={d.wallet_name}>
                                        <Form.Group as={Row}>
                                            <Col sm={10}>
                                                <Form.Check
                                                    type="radio"
                                                    label= {d.wallet_name}
                                                    name="wallet"
                                                    required="required"
                                                    value={this.state.wallet}
                                                    onChange={this.onValueChange}
                                                />
                                            </Col>
                                        </Form.Group>
                                    </div>
                                    );
                                })}
                            </fieldset>

                            <label>Amount</label>
                            <input type="text" pattern="[0-9]*" className="form-control" placeholder="Enter amount" required="required"  name="amount" value={this.state.amount} onChange={this.onValueChange} />
                        </div>




                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Add Money</button>
                        <br/>
                        <p className=" text-right">
                            <a href="/home">Go Back</a>
                        </p>
                    </form>
                </div>
            </div>
        );
    }

}
export default Credit;
