import React, {Component, useState} from "react";
import { ToggleButton , Button } from 'react-bootstrap';

import './home.css';
import {Redirect} from "react-router";
import {CheckLogin} from "./login";

class Home extends Component
{
    constructor(props) {
        super(props);
        const token = localStorage.getItem("token")


        this.state={
            loggedIn:false,
            data:props.data
        }


    }

    componentDidMount()
    {
        this.setState({
            loggedIn : this.props.loggedIn
        })
    }

    render()
    {

        // if(this.state.loggedIn == false ){
        //     return <Redirect to="/"/>
        // }

        const df=this.props.data

        return(
            <div className="bg-cover page-holder bg-transparent" style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>


                    <div className="text-right mr-2">
                        <br/><br/><br/>
                        <b>Welcome , {this.props.user} </b>
                        <br/>
                        <div className="btn-group btn-group-lg green" role="group" aria-label="Basic example">
                            {/*<button type="button" className="btn green btn-secondary" onClick={this.handleDelete.bind(this)}>Delete User</button>*/}

                            your current wallets are:

                                {df.map((postData) => {
                                    console.log(postData);
                                    return (
                                                 <p>
                                                    <button type="button" className="btn green btn-secondary">{postData.wallet_name}</button>
                                                </p>
                                    );
                                })}




                        </div>

                        <br/><br/>

                    </div>


                <div className="main-block text-center " >


                    {/*<div className="left-part card box_shw2 border-0 px-3 rounded-2 mb-3 w_100 py-4  mt-5">*/}
                    {/*    <h3><b>Your Current Balance : </b> Rs.10,000.00</h3>*/}
                    {/*</div>*/}
                    {/*<br/>*/}
                    {/*<br/>*/}
                <header><h1><b>Choose a Service</b></h1></header>
                    <br/>
                    <div >
                        <div className="row justify-content-center">
                            <div className="col-auto">

                        <table  width="600" height="200" cellPadding="30" cellSpacing="20" >
                            <thead>
                            <tr>
                                <td>
                                    <a href={'/credit'} ><Button className="btn btn-block green" variant="secondary" size="lg" >Credit Amount</Button></a>
                                </td>
                                <td>
                                    <a href={'/debit'} ><Button className="btn btn-block green" variant="secondary" size="lg">Debit Amount</Button></a>
                                </td>
                            </tr>
                            <tr>
                                <td>
                                    <a href={'/status'} ><Button className="btn btn-block green"  variant="secondary" size="lg" >Manage Status</Button></a>
                                </td>
                                <td>
                                    <a href={'/wallets'} ><Button className="btn btn-block green"  variant="secondary" size="lg" >Add/Del Wallet</Button></a>
                                </td>
                            </tr>
                            {/*<tr>*/}
                            {/*    <td>*/}
                            {/*        <a href={'/statement'} ><Button className="btn btn-block" variant="secondary green" size="lg" >Generate Statement</Button></a>*/}
                            {/*    </td>*/}
                            {/*    <td>*/}
                            {/*        <a href={'/default'} ><Button className="btn btn-block" variant="secondary green" size="lg" >Change Default Wallet</Button></a>*/}
                            {/*    </td>*/}
                            {/*</tr>*/}
                            <tr>
                                <td colSpan="2">
                                    <Button className="btn btn-block" variant="secondary green" size="lg" ><span className="fa fa-arrow-circle-down fa-lg"></span> Download Daily Report</Button>
                                </td>
                            </tr>
                            </thead>

                        </table>
                </div>
                        </div>
                    </div>

                </div>

            </div>
        )

}


}
export default Home;
