import React, { Component } from "react";
import {Redirect} from "react-router-dom";
import store from 'store';
import './body.css'


class Body extends Component
{
    constructor(props)
    {
        super(props)

        let loggedIn = false

        this.state = {
            username:'',
            loggedIn,
            users:[]
        };

        this.onChange = this.onChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    onChange(e)
    {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }



    handleSubmit(event) {

        event.preventDefault();
        const username = this.state.username
        const value = {username}
        console.log('Current State is: ' + JSON.stringify(value));

        const api= '/login';
        const request = {
            method: 'POST',
            headers: { 'Accept':'application/json','Content-Type': 'application/json' },
            body: JSON.stringify(value)
        };

        fetch(api,request)
            .then((response) => console.log(response))
            .then((data)=>console.log('data is submitted'))
            .then(this.setState({loggedIn:true}))
            .catch(err => console.log(err))




        // localStorage.setItem("token", "anewtoken")
        // this.setState({
        //     loggedIn: true
        // });
    }

    //     // console.log("you're logged in!");
    //     // store.set('loggedIn', true);
    //     //
    //     // const {history} =this.props;
    //     // history.push('/home');


    render()
    {
        const {users}=this.state;

        if(this.state.loggedIn)
        {
            return <Redirect to="/home" user={this.state.username}/>
        }
        return(
            <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                <div className="container py-5 ">
                    <header className="text-center text-grey py-5 animate__animated animate__fadeInUpBig ">
                        <h4 className="display-4 font-weight-bold mb-4">Wallet As A Service</h4>

                    </header>
                        <h2>Choose a user to Login</h2>
                        {users.map(item => (
                            <li key={item.ID}>
                                <b > {item.Username}</b >
                            </li>
                        ))}

                    <div className="box">
                            <h2>Login to a user</h2>
                        <ul>

                        </ul>
                        <form onSubmit={this.handleSubmit} >

                            <div className="inputBox">
                                <input type="text" name="username" required  value={this.state.username}  onChange={this.onChange}/>
                                    <label>Username</label>
                            </div>

                            <button type="submit" className="btn btn-lg btn-primary ">Sign In</button>
                            <br/><br/>
                        </form>
                            <div className="text-right">
                                <p style={{color:'white'}}><span>Add a new User </span>
                                <a href="/register"><button type="submit" className="btn btn-lg btn-primary ">Register</button></a>
                                </p>
                            </div>

                    </div>


                </div>

            </div>
        )
    }
}
export default Body;

