import React, { Component } from "react";
import  './register.css';


class Register extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            name: '',
            username: '',
            age: '',
            contact: '',
            kyc:'',
            password:''
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        console.log('Current State is: ' + JSON.stringify(this.state));
        alert('Current State is: ' + JSON.stringify(this.state));
        event.preventDefault();
    }

    render() {
        return (
            <div>
                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                <form onSubmit={this.handleSubmit}>

                <h3>Sign Up</h3>

                <div className="form-group">
                    <label>Name</label>
                    <input type="text" className="form-control" placeholder="First name" id="name" name="name" value={this.state.name}
                           onChange={this.handleInputChange} required="required"/>
                </div>

                <div className="form-group">
                    <label>User name</label>
                    <input type="text" className="form-control" placeholder="username" name="username" value={this.state.username}
                           onChange={this.handleInputChange} required="required"/>
                </div>

                <div className="form-group">
                    <label>Age</label>
                    <input type="" className="form-control" placeholder="Enter age" name="age" value={this.state.age}
                           onChange={this.handleInputChange} required="required"/>
                </div>

                <div className="form-group">
                        <label>Contact</label>
                        <input type="number" className="form-control" placeholder="Enter number" name="contact" value={this.state.contact}
                               onChange={this.handleInputChange} required="required"/>
                </div>

                <div className="form-group">
                        <label>KYC Details</label>
                        <input type="text" className="form-control" placeholder="enter kyc number" name="kyc" value={this.state.kyc}
                               onChange={this.handleInputChange} required="required"/>
                </div>


                    <div className="form-group">
                    <label>Password</label>
                    <input type="password" className="form-control" placeholder="Enter password" name="password" value={this.state.password}
                           onChange={this.handleInputChange} required="required"/>
                </div>

                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                <p className="text-right">
                    Already registered <a href="/log">sign in?</a>
                </p>

                </form>
                </div>


            </div>
        );
    }
}
export default Register;

