import React, {Component} from "react";
import {Form ,Modal ,Col ,Row ,Button} from 'react-bootstrap';

class AddModal extends Component{
    constructor(props) {
        super(props);

        this.state={
            walletname:'',
            u_id:props.u_id,
            username:props.username,
            loggedIn:props.loggedin,
            data:props.data
        }

        this.onChange = this.onChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);


    }


    onChange(e)
    {
        this.setState({
            walletname : e.target.value
        })
    }

    handleSubmit(e) {
        e.preventDefault();

        const {walletname} = this.state
        const value = {walletname}
        console.log('Current State is: ' + JSON.stringify(value));

        const apiUrl = '/users';
        const requestOptions = {
            method: 'POST',
            headers: {'Accept': 'application/json', 'Content-Type': 'application/json'},
            body: JSON.stringify(value)
        };


        fetch(apiUrl, requestOptions)
            .then(response => {
                if (response.status === 500) {
                    response.json().then((object) => {
                        {
                            this.setState({errorMessage: object.error})
                        }
                    })
                } else {
                    response.json().then((object) => {
                        {
                            this.setState(
                                {
                                    wallet_name: '',
                                })
                            alert('success')

                        }
                    })
                }
            })
    }

    render() {
        return (
            <Modal
                {...this.props}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Add a Wallet
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <label>Wallet Name</label>
                            <input type="text" className="form-control" placeholder="Enter wallet name"
                                   name="walletname"  required="required" value={this.state.walletname} onChange={this.onChange}/>
                        </div>


                        <button type="submit" className="btn btn-primary btn-block">Add Wallet</button>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.props.onHide}>Back</Button>
                </Modal.Footer>
            </Modal>
        )
    }

}

export default AddModal
