import React, { Component } from "react";
import {LocalForm ,Control,Errors} from "react-redux-form";


const required = (val) => val && val.length;
const maxLength= (len) => (val) => !(val) || (val.length <= len )
const minLength= (len) => (val) => (val) && (val.length >= len )
const isNumber = (val) => !isNaN(Number(val));
const validEmail=(val) => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val)

export default class Login extends Component
{
    constructor(props)
    {
        super(props);
        this.state = {
            username:'',
            loggedIn,
            users:[]
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }

    handleSubmit(values) {
        console.log( JSON.stringify(values));
        const {history} =this.props;
        history.push('/home');
    }

    render() {
        const {users}=this.state;
        return (
                <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                    backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                            <br/><br/><br/><br/>
                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                        <h2>Your Current Users are :</h2>
                        {users.map(item => (
                            <li key={item.ID}>
                                <b > {item.Username}</b >
                            </li>
                        ))}
                            <LocalForm onSubmit={(values)=>this.handleSubmit(values)}>
                                <h3>Log in to a User</h3>

                                <div className="form-group">
                                    <label>Username</label>
                                    <Control.text  model=".username" className="form-control" placeholder="Enter username" required="required" id="name" name="name" />
                                </div>
                                <br/>
                                <button type="submit" className="btn btn-primary btn-block">Login</button>
                                <br/>
                                <p className="forgot-password text-right">
                                    New User ? <a href="/register"> Register here</a>
                                </p>
                            </LocalForm>
            </div>
            </div>
        );
    }
}
