import React,{Component} from "react";
import {Button, Form} from "react-bootstrap";

import Switch from "react-switch";
import Home from "./home";
class Status extends Component
{
        constructor(props)
        {
            super(props);

            this.state=
                {
                u_id:props.id,
                username:props.username,
                loggedIn:props.loggedIn,
                data:props.data,
                walletName:'',
                home:false,
                blockedUsers:[],
                checkCount: 0,
                checkboxValid:false
                }

                this.onValueChange = this.onValueChange.bind(this);
                this.handleSubmit = this.handleSubmit.bind(this);
                this.handleChange = this.handleChange.bind(this);
                this.goBack=this.goBack.bind(this);
        }

        onValueChange(event)
        {
            var joined
            if(event.target.checked)
            {
                this.setState((prevState) => ({checkCount: prevState.checkCount + 1}))
                joined = this.state.blockedUsers.concat(event.target.value);
                this.setState({blockedUsers: joined})
                console.log(this.state.blockedUsers)
            }
            else {
                this.setState((prevState) => ({checkCount: prevState.checkCount - 1}))
                joined = this.state.blockedUsers.splice(-1,1);
                this.setState({blockedUsers: joined})
                console.log(this.state.blockedUsers)
            }
        }

        handleSubmit(event)
        {
            event.preventDefault()
            var st = this.state.blockedUsers
            if (this.state.checkCount === 0) {
                // this.setState({checkboxValid: true})
                alert("nothing is selected")
            }
            else {
                var final = {blockNames: (st)}
                console.log(JSON.stringify(final))

                const apiUrl = '/block/'+this.state.u_id;
                const requestOptions = {
                    method: 'POST',
                    headers: { 'Accept':'application/json','Content-Type': 'application/json' },
                    body: JSON.stringify(final)
                };

                // fetch(apiUrl,requestOptions)
                //     .then(res => res.text()) // or res.json()
                //     .then(res => alert(res))
                //     .catch(err=>console.log(err))

            }

            event.target.reset();

        }

        goBack(e)
        {
            e.preventDefault()
            this.setState({home:true})

        }
        handleChange(checked)
        {
        this.setState({ checked });
        }

    render()
        {

        const data=this.state.data;
        let i=0;

        if(this.state.home)
        {
            console.log(this.props.username , this.props.loggedIn  ,this.props.data)
            return <Home username={this.props.username} loggedIn={this.props.loggedIn} data={this.props.data} />
        }

        return(

            <div className="page-holder bg-cover " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
                <header className="text-center text-grey py-5">
                    <div style={{backgroundColor:'white'}}>
                        <br/><h1 className="font-weight-bold">Block / Unblock Wallets</h1><br/>
                    </div>


                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <h3>Choose Wallet to Block</h3>
                        <br/>
                            <Form.Group id="formGridCheckbox">
                            {data.map((d)=> (
                                <Form.Check type="checkbox" label={d.wallet_name} key={i++} value={d.wallet_name}
                                            onClick={this.onValueChange}/>
                            ))
                            }
                            </Form.Group>


                        </div>
                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Submit</button>
                        <br/>
                    </form>

                        <p className=" text-right">
                            <Button onClick={this.goBack}>Go Back</Button>
                        </p>
                    </div>
                </header>
            </div>
        )
    }
}

export default Status;
