import React, { Component } from "react";
import {Redirect} from "react-router-dom";
import store from 'store';
import './body.css'
import Home from "./home";
import {Button, Form} from "react-bootstrap";


class Deleteusers extends Component
{
    constructor(props)
    {
        super(props)
        this.state={
            users:[],
            deluser:'',
            DeleteUsers:[]
        }
        this.onChange= this.onChange.bind(this);
        this.onSubmit= this.onSubmit.bind(this);
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }

    onChange(event)
    {
        var joined
        if(event.target.checked)
        {
                joined = this.state.DeleteUsers.concat(parseInt(event.target.value));
                this.setState({DeleteUsers: joined})
                console.log(this.state.DeleteUsers)
        }
    }

    onSubmit(event)
    {
            event.preventDefault()
            var st=this.state.DeleteUsers
            var final={DeleteUsers : (st)}
            console.log(JSON.stringify(final))

            fetch('/deleteUsers',{
                method: 'DELETE',
                headers: { 'Accept':'application/json','Content-Type': 'application/json' },
                body: JSON.stringify(final)
            })
                .then(res => res.text()) // or res.json()
                .then(res => console.log(res))
    }


    render() {
        const {users}=this.state;
        return (
            <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                <br/><br/><br/><br/>

            <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

            <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <h1 >Delete Users</h1>
                       <hr/>
                        <label>Choose Users to Delete</label>

                        <Form.Group id="formGridCheckbox">
                            {users.map(item => (
                            <Form.Check type="checkbox" label={item.Username} key={item.ID} value={item.ID} onClick={this.onChange}/>
                                ))}
                        </Form.Group>


                    </div>
                    <button type="submit" className="btn btn-primary btn-block">Delete Users</button>

                </form>
                <br/>
                <p className=" text-right">
                    <a href="/home">Go Back</a>
                </p>

            </div>
            </div>
        );
    }
}

export default Deleteusers
