import React, { Component } from "react";
import {Redirect} from "react-router-dom";
import store from 'store';
import './body.css'


class Body extends Component
{
    constructor(props)
    {
        super(props)
        let loggedIn = false

        this.state = {
            loggedIn,
            users:[]
        };


        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => console.log(response.json()))
            .then((data) => {this.setState({users:data});})
            .then(console.log(users.id))
            .catch(err => console.log(err))
    }

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    handleSubmit(event) {

        event.preventDefault();

           localStorage.setItem("token","anewtoken")
            this.setState({
                loggedIn: true
            });


        //
        // console.log("you're logged in!");
        // store.set('loggedIn', true);
        //
        // const {history} =this.props;
        // history.push('/home');


    }

    render()
    {
        const {users}=this.state;

        if(this.state.loggedIn)
        {
            return <Redirect to="/home"/>
        }
        return(
            <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                <div className="container py-5 ">
                    <header className="text-center text-grey py-5 animate__animated animate__fadeInUpBig ">
                        <h4 className="display-4 font-weight-bold mb-4">Wallet As A Service</h4>
                    </header>

                    <div className="box">
                            <h2>Login to a user</h2>
                        <ul>
                            {users.map(item => (
                                <li key={item.ID}>
                                    {item.username}
                                </li>
                            ))}
                        </ul>
                        {/*<form onSubmit={this.handleSubmit} >*/}

                        {/*    <div className="inputBox">*/}
                        {/*        <input type="text" name="username" required />*/}
                        {/*            <label>Username</label>*/}
                        {/*    </div>*/}
                        {/*    <div className="inputBox">*/}
                        {/*        <input type="password" name="password" required />*/}
                        {/*            <label>Password</label>*/}
                        {/*    </div>*/}
                        {/*  <button type="submit" className="btn btn-lg btn-primary ">Sign In</button>*/}
                        {/*    <br/><br/>*/}
                        {/*</form>*/}
                            <div className="text-right">
                                <p style={{color:'white'}}><span>Add a new User</span>
                                <a href="/register"><button type="submit" className="btn btn-lg btn-primary ">Register</button></a>
                                </p>
                            </div>

                    </div>


                </div>

            </div>
        )
    }
}
