import React, { Component } from "react";
import axios from 'axios';

import  './register.css';
import {LocalForm ,Control,Errors} from "react-redux-form";

const required = (val) => val && val.length;
const maxLength= (len) => (val) => !(val) || (val.length <= len )
const minLength= (len) => (val) => (val) && (val.length >= len )
const isNumber = (val) => !isNaN(Number(val));
const validEmail=(val) => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val)

class Register extends Component
{
    constructor(props)
    {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);

    }

    handleSubmit(value) {

        const value1={"username":"df1vd","name":"df","age":11,"contact":1234567890,"password":"dwq","kyc_details":123}
        console.log('Current State is: ' + JSON.stringify(value));

        const apiUrl = 'http://localhost:8080/users';
        const requestOptions = {
            method: 'POST',
            headers: { 'Accept':'application/json','Content-Type': 'application/json' },
            body: JSON.stringify(value1)
        };
        fetch(apiUrl,requestOptions)
            .then((response) => console.log(response))
            .then((data)=>console.log(data))
            .catch(err => console.log(err))


        // this.signup(value);
        // fetch("http://localhost:8080/users")
        //     .then(response => {
        //         console.log(response)
        //     })
        //     .catch(err => console.log(err))

    }

    //
    // signup=(User)=>{
    //     return fetch("http://localhost:8080/users",{
    //         method:"POST",
    //         headers:{
    //             Accept:"application/json",
    //             "Content-type":"application/json"
    //         },
    //         body:JSON.stringify(User)
    //     })
    //         .then(response=>{
    //             return response.json()
    //         })
    //         .catch(err=>console.log(err))
    // }


    render() {
        return (
            <div  className="page-holder " style={{backgroundColor: '#c2eaba;',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}><br/><br/>

                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                <LocalForm onSubmit={(values)=>this.handleSubmit(values)}>

                <h4>Sign Up</h4>

                <div className="form-group">


                        <label>User name</label>
                        <Control.text model=".username"
                                      className="form-control"
                                      placeholder="First name"
                                      id="username" name="username"
                                      validators={{
                                            required,minLength:minLength(3) , maxLength:maxLength(10)
                                      }}
                        />
                        <Errors model=".username" className="text-danger" show="touched"
                                messages={{
                                    required: 'Required \n ',
                                    minLength:' Must be greater than 2 ',
                                    maxLength:' Must be less than 10 '
                                }}
                        />


                        <label>Name</label>
                        <Control.text model=".name" className="form-control" placeholder="name" name="name"  required="required"/>


                        <label>Age</label>
                        <Control.text model=".age" className="form-control" placeholder="Enter age" name="age" required="required"/>

                        <label>Contact</label>
                        <Control.text model=".contact" pattern="[0-9]*" minLength={10} maxLength={10} className="form-control" placeholder="Enter number" name="contact" required="required"/>

                        <label>Password</label>
                        <Control.password model=".password" className="form-control" placeholder="Enter password" name="password" required="required"/>

                        <label>KYC Details</label>
                        <Control.text model=".kyc_details" className="form-control" placeholder="enter kyc number" name="kyc_details" />


                </div>

                <button type="submit" className="btn btn-primary btn-block">Sign Up</button>
                <p className="text-right">
                    Already registered <a href="/log">sign in?</a>
                </p>

                </LocalForm>
                </div>


            </div>
        );
    }
}
export default Register;

