import './home.css';

function Home()
{
    const [radioValue, setRadioValue] = useState('1');

    const radios = [
    { name: 'Wallet 1', value: '1' },
    { name: 'Wallet 2', value: '2' },
    { name: 'Wallet 3', value: '3' },
    ];


        return(
            <div className=" page-holder bg-cover" style={{backgroundColor:'#FFE4E1'}}>
