                {/*        </header>*/}

                {/*    </div>*/}
                    <div className="text-right">
                        <b style={{color: '#FFE4E1'}}>Your Current wallets:  </b>
                        <ButtonGroup size="lg">
                            <Button>W1</Button>
                            <Button>W2</Button>
                            <Button>W3</Button>
                        </ButtonGroup>
                        <br/><br/>
                        <a href={'/wallets'} ><Button variant="flat" >Add a new Wallet</Button></a>

                    </div>
                {/*</div>*/}

                <div className="main-block " >


