import React, { Component } from "react";

import {LocalForm ,Control,Errors} from "react-redux-form";
import {Redirect} from "react-router";
import Home from "./home";


// const required = (val) => val && val.length;
// const maxLength= (len) => (val) => !(val) || (val.length <= len )
// const minLength= (len) => (val) => (val) && (val.length >= len )
// const isNumber = (val) => !isNaN(Number(val));
// const validEmail=(val) => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val)


export default class Login extends Component
{
    constructor(props)
    {
        super(props);

        this.state = {
            username:'',
            loggedIn:false,
            users:[],
            errorMessage:''
        };

        this.onValueChange = this.onValueChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }


    onValueChange(event) {
        this.setState({
            username : event.target.value
        });
    }



    handleSubmit(event) {
        event.preventDefault();
        const username = this.state.username
        const value = {username}
        console.log('Current State is: ' + JSON.stringify(value));


        const api= '/login';
        const request = {
            method: 'POST',
            headers: { 'Accept':'application/json','Content-Type': 'application/json' },
            body: JSON.stringify(value)
        };

        fetch(api,request)
            .then(response => {
                    if (response.ok) {
                        return response;
                    }
                    else {
                        var error = new Error('Error ' + response.status + ': ' + response.statusText);
                        error.response = response;
                        throw error;
                    }
                },
                error => {
                    var errmess = new Error(error.message);
                    throw errmess;
                })
            .then((response) => console.log(response))
            .then((data)=>console.log('logged in'))
            .then(this.setState({loggedIn:true}))
            .catch(err => console.log(err))


        // console.log( JSON.stringify(values));
        // const {history} =this.props;
        // history.push('/home');
    }

    render() {
        const {users}=this.state;
        const username = this.state.username
        if(this.state.loggedIn === true)
            return <Home user={this.state.username} loggedIn={this.state.loggedIn} />


        return (
                <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                    backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                            <br/><br/><br/><br/>
                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">
                        <h3>Current Users</h3>
                        <hr/>
                        <div className="form-group">

                            <label><b>Choose a user to Login</b></label>


                        <form onSubmit={this.handleSubmit}>
                        <br/>
                        {users.map(item => (

                            <div className="radio" key={item.uid}>
                                <label>
                                    <input
                                        type="radio"
                                        name="radio"
                                        required="required"
                                        value={item.username}
                                        onChange={this.onValueChange}
                                    />
                                    <span> {item.username}  </span>
                                    {/*<span className="second-word-formatting"> {item.name}</span>*/}
                            </label>

                        </div>
                            ))}
                            { this.state.errorMessage &&
                            <h3 className="error"> { this.state.errorMessage } </h3> }
                                <br/>
                                <button type="submit" className="btn btn-primary btn-block">Login</button>
                                <br/>

                            <p className=" text-right">
                                <a href="/">Go Back</a>
                            </p>
                            </form>
                        </div>
            </div>
            </div>
        );
    }
}
