import React from "react";
import {Form ,Modal ,Col ,Row ,Button} from 'react-bootstrap';

function MyVerticallyCenteredModal(props) {
    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
        >
            <Modal.Header>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add a Wallet
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                   <form>


                        <div className="form-group">
                            <label>Wallet Name</label>
                            <input type="text" className="form-control" placeholder="Enter wallet name" required="required" id="walletname" name="walletname" />
                        </div>

                        <div className="form-group">
                            <label>Amount</label>
                            <input type="password" className="form-control" placeholder="Enter amount" required="required" id="amount" name="amount"/>
                        </div>


                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Add Wallet</button>
                        <br/>

                    </form>

            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Back</Button>
            </Modal.Footer>
        </Modal>
    );
}
function Wallets()
{
    const [modalShow, setModalShow] = React.useState(false);


        return (

            <div style={{backgroundColor:'#FFE4E1'}} className="text-center page-holder bg-cover">
                <br/><br/><br/>
                <header>
                    <h1><b> Manage Wallets </b></h1>
                </header>

                <header className="text-center text-white py-5">
                <div className="container">

                                <Button className="btn floatleft btn-xxl"  onClick={() => setModalShow(true)} variant="secondary" size="lg" style={{fontsize:'250px'}}>Add a Wallet</Button>

                                <Button className="btn floatright btn-xxl" onClick={() => setModalShow(true)} variant="secondary" size="lg" >Delete a Wallet</Button>
                    <MyVerticallyCenteredModal
                        show={modalShow}
                        onHide={() => setModalShow(false)}
                    />
                </div>
                </header>







            </div>
        );


}
export default Wallets;
