import React, {Component} from "react";
import {Form ,Modal ,Col ,Row ,Button} from 'react-bootstrap';

class DelModal extends Component{
    constructor(props) {
        super(props)
        this.state={
            walletname:'',
            u_id:props.u_id,
            username:props.username,
            loggedin:props.loggedin,
            data:props.data,
            delete_wallets:[],
            checkCount: 0,
            checkboxValid:false
        }

        this.onValueChange = this.onValueChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);


    }


    onValueChange(event)
    {
        var joined
        if(event.target.checked)
        {
            this.setState((prevState) => ({checkCount: prevState.checkCount + 1}))
            joined = this.state.delete_wallets.concat(event.target.value);
            this.setState({delete_wallets: joined})
            console.log(this.state.delete_wallets)
        }
        else
        {
            this.setState((prevState) => ({checkCount: prevState.checkCount - 1}))
            joined = this.state.delete_wallets.splice(-1,1);
            this.setState({delete_wallets: joined})
            console.log(this.state.delete_wallets)
        }
    }



    handleSubmit(event)
    {
        var st = this.state.delete_wallets
        console.log(st)
        event.preventDefault()

        // if (this.state.checkCount === 0) {
        //     // this.setState({checkboxValid: true})
        //     alert("nothing is selected")
        // }
        // else {
        //     var final = {DeleteNames: (st)}
        //     console.log(JSON.stringify(final))
        //
        //
        //     const apiUrl = '/delete/'+this.state.u_id;
        //     const requestOptions = {
        //         method: 'DELETE',
        //         headers: { 'Accept':'application/json','Content-Type': 'application/json' },
        //         body: JSON.stringify(final)
        //     };
        //
        //     fetch(apiUrl,requestOptions)
        //         .then(res => res.text()) // or res.json()
        //         .then(res => alert(res))
        //         .then(this.setState({delete_wallets:[]}))
        //         .catch(err=>console.log(err))
        //
        // }

        event.target.reset();

    }



    render() {
        const data=this.state.data;
        let i=0;
        return (
            <Modal
                {...this.props}
                aria-labelledby="contained-modal-title-vcenter"
                centered>
                <Modal.Header>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Delete Wallets
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group">
                            <label>Choose Wallets to Delete</label>

                            <Form.Group id="formGridCheckbox">
                                {data.map((d)=> (
                                    <Form.Check type="checkbox" label={d.wallet_name} key={i++} value={d.wallet_name}
                                                onClick={this.onValueChange}/>
                                ))
                                }
                            </Form.Group>


                        </div>
                        <button type="submit" className="btn btn-primary btn-block">Delete Wallet</button>
                    </form>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={this.props.onHide}>Back</Button>
                </Modal.Footer>
            </Modal>
        );
    }
}


export default DelModal;
