import React, { Component } from "react";
import axios from 'axios';

import  './register.css';
import Body from "./body";
import {Redirect} from "react-router";
import {Errors} from "react-redux-form";
// import {LocalForm ,Control,Errors} from "react-redux-form";
//
// const required = (val) => val && val.length;
// const maxLength= (len) => (val) => !(val) || (val.length <= len )
// const minLength= (len) => (val) => (val) && (val.length >= len )
// const isNumber = (val) => !isNaN(Number(val));
// const validEmail=(val) => /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val)

class Register extends Component
{
    constructor(props)
    {
        super(props);

        this.state={
            username:'',
            name:'',
            age: '',
            contact:'',
            password:'',
            kyc_details:'',
            registered:false,
            errorMessage:''
        }

        this.onChange = this.onChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    onChange(e)
    {
        this.setState({
            [e.target.name]: e.target.type === 'number' ? parseInt(e.target.value) : e.target.value
        })
    }

    handleSubmit(e) {
        e.preventDefault()
        const { username , name , age, contact ,password , kyc_details} = this.state
        const value = { username , name , age, contact }
        console.log('Current State is: ' + JSON.stringify(value));

        const apiUrl = '/users';
        const requestOptions = {
            method: 'POST',
            headers: { 'Accept':'application/json','Content-Type': 'application/json' },
            body: JSON.stringify(value)
        };

        fetch(apiUrl,requestOptions)
            .then(function(response) {
                if (response.status === 500) {
                    response.json().then(function(object) {
                        alert(object.error)
                    })
                } else if (response.status === 200) {
                    response.json().then(function(object) {
                        alert('success')
                    })
                }
            })



    }



    render() {

        if(this.state.registered === true)
            return <Redirect to="/"/>

        return (
            <div  className="page-holder " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}><br/><br/>

                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                <form onSubmit={this.handleSubmit}>

                <h4>Add New User</h4>

                <div className="form-group">


                        <label>User name</label>
                        <input type="text" className="form-control" placeholder="username"  name="username" required="required"  value={this.state.username}  onChange={this.onChange}/>

                        <label>Name</label>
                        <input type="text" className="form-control" placeholder="name" name="name"  required="required" value={this.state.name} onChange={this.onChange}/>


                        <label>Age</label>
                        <input type="number" className="form-control" placeholder="Enter age" name="age" required="required" value={this.state.age} onChange={this.onChange}/>

                        <label>Contact</label>
                        <input type="text" pattern="[0-9]*"  className="form-control" placeholder="Enter number" name="contact" required="required" value={this.state.contact} onChange={this.onChange}/>

                        {/*<label>Password</label>*/}
                        {/*<input type="password" disabled className="form-control" placeholder="Enter password" name="password" required="required" value={this.state.password} onChange={this.onChange}/>*/}

                        {/*<label>KYC Details</label>*/}
                        {/*<input type="number" disabled className="form-control" placeholder="enter kyc number" name="kyc_details"  value={this.state.kyc_details} onChange={this.onChange}/>*/}


                </div>

                <button type="submit" className="btn btn-primary btn-block">Create User</button>
                    <p className=" text-right">
                        <a href="/">Go Back</a>
                    </p>

                </form>
                </div>


            </div>
        );
    }
}
export default Register;

