import React, {Component, useState} from "react";
import { ToggleButton , Button } from 'react-bootstrap';

import './home.css';
import {Redirect} from "react-router";
import {CheckLogin} from "./login";
import Credit from "./credit";
import Debit from "./debit";
import Wallets from "./Wallets";
import Status from "./status";

class Home extends Component
{
    constructor(props) {
        super(props);

        this.state={
            username:props.username,
            loggedIn:props.loggedIn,
            data:props.data,
            uid:props.uid,
            credit:false,
            debit:false,
            status:false,
            wallets:false
        };

        const token = localStorage.getItem("token")
        if(token == null)
        {
            this.setState({loggedIn:false})
        }

        this.HandleCredit = this.HandleCredit.bind(this);
        this.HandleDebit = this.HandleDebit.bind(this);
        this.HandleStatus = this.HandleStatus.bind(this);
        this.HandleWallets = this.HandleWallets.bind(this);


    }

    componentDidMount()
    {
        this.setState({
            loggedIn : this.props.loggedIn
        })
    }

    HandleCredit(event){
        event.preventDefault();
        const df=this.props.data
        let uid="";
        df.map((postData) => {
            uid=postData.user_id
        })
        this.setState({credit:true , uid:uid});
    }

    HandleDebit(event){
        event.preventDefault();
        const df=this.props.data
        let uid="";
        df.map((postData) => {
            uid=postData.user_id
        })
        this.setState({debit:true , uid:uid});
    }

    HandleStatus(event){
        event.preventDefault();
        const df=this.props.data
        let uid="";
        df.map((postData) => {
            uid=postData.user_id
        })
        this.setState({status:true , uidd:uid});
    }

    HandleWallets(event){
        event.preventDefault();
        const df=this.props.data
        let uid="";
        df.map((postData) => {
            uid=postData.user_id
        })
        this.setState({wallets:true , iid:uid});
    }


    render()
    {

        if(this.state.loggedIn === false) {
            return <Redirect to="/"/>
        }

        const df=this.props.data
        let i=0
        if(this.state.credit ===true )
        {
            return <Credit username={this.state.username} loggedIn={this.state.loggedIn} id={this.state.uid} data={this.props.data} />
        }
        else if(this.state.debit ===true )
        {
            return <Debit username={this.state.username} loggedIn={this.state.loggedIn} id={this.state.uid} data={this.props.data} />
        }
        else if(this.state.status ===true )
        {
            return <Status username={this.state.username} loggedIn={this.state.loggedIn} id={this.state.uid} data={this.props.data} />
        }
        else if(this.state.wallets ===true )
        {
            return <Wallets username={this.state.username} loggedIn={this.state.loggedIn} id={this.state.uid} data={this.props.data} />
        }


                return(
                <div className="bg-cover page-holder bg-transparent" style={{backgroundColor: '#c2eaba',
                    backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>



                        <br/><br/><br/>
                        <b>Welcome , {this.state.username} </b>
                        <br/>
                            {/*<button type="button" className="btn green btn-secondary" onClick={this.handleDelete.bind(this)}>Delete User</button>*/}

                    <div className="text-right mr-2">
                        {this.props.data.length > 0 &&
                        <b>Your current wallets are: </b>  }
                            <br/>

                        <div className="btn-group" role="group" aria-label="Basic example" key={i++}>

                        {df.map((postData) => {
                                console.log(postData);
                                return (
                                    <div className="btn-group" role="group" aria-label="Basic example" key={i++}>
                                        {(postData.is_active) &&
                                        <button type="button" className="btn green btn-secondary">{postData.wallet_name}</button>
                                        }

                                        {(!postData.is_active) &&
                                        <button type="button" className="btn green btn-secondary" disabled="disabled">{postData.wallet_name}</button>
                                        }
                                    </div>
                                );
                            })}

                        </div>




                        <br/><br/>

                    </div>


                    <div className="main-block text-center " >


                        {/*<div className="left-part card box_shw2 border-0 px-3 rounded-2 mb-3 w_100 py-4  mt-5">*/}
                        {/*    <h3><b>Your Current Balance : </b> Rs.10,000.00</h3>*/}
                        {/*</div>*/}
                        {/*<br/>*/}
                        {/*<br/>*/}
                        <header><h1><b>Choose a Service</b></h1></header>
                        <br/>
                        <div >
                            <div className="row justify-content-center">
                                <div className="col-auto">

                                    <table  width="600" height="200" cellPadding="30" cellSpacing="20" >
                                        <thead>
                                        <tr>
                                            <td>
                                                <Button className="btn btn-block green" variant="secondary" size="lg" onClick={this.HandleCredit}>Credit Amount</Button>
                                            </td>
                                            <td>
                                                <Button className="btn btn-block green" variant="secondary" size="lg" onClick={this.HandleDebit}>Debit Amount</Button>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>
                                                <Button className="btn btn-block green"  variant="secondary" size="lg" onClick={this.HandleStatus}>Manage Status</Button>
                                            </td>
                                            <td>
                                               <Button className="btn btn-block green"  variant="secondary" size="lg" onClick={this.HandleWallets}>Add/Del Wallet</Button>
                                            </td>
                                        </tr>
                                        {/*<tr>*/}
                                        {/*    <td>*/}
                                        {/*        <a href={'/statement'} ><Button className="btn btn-block" variant="secondary green" size="lg" >Generate Statement</Button></a>*/}
                                        {/*    </td>*/}
                                        {/*    <td>*/}
                                        {/*        <a href={'/default'} ><Button className="btn btn-block" variant="secondary green" size="lg" >Change Default Wallet</Button></a>*/}
                                        {/*    </td>*/}
                                        {/*</tr>*/}
                                        <tr>
                                            <td colSpan="2">
                                                <Button className="btn btn-block" variant="secondary green" size="lg" ><span className="fa fa-arrow-circle-down fa-lg"></span> Download Daily Report</Button>
                                            </td>
                                        </tr>
                                        </thead>

                                    </table>
                                </div>
                            </div>
                        </div>

                    </div>

                </div>
                )

  }


}
                export default Home;
