    //     history.push('/home');
    // }

    render() {
        return (
                <div className="page-holder bg-cover" style={{backgroundColor: '#c2eaba',
                    backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>

                            <br/><br/><br/><br/>
                    <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

                        <div className="box">
                                <h2>Login to a user</h2>
                            <ul>

                            </ul>
                            <form onSubmit={this.handleSubmit} >

                                <div className="inputBox">
                                    <input type="text" name="username" required  value={this.state.username}  onChange={this.onChange}/>
                                        <label>Username</label>
                                </div>

                                <button type="submit" className="btn btn-lg btn-primary ">Sign In</button>
                                <br/><br/>
                            </form>
                                <div className="text-right">
                                    <p style={{color:'white'}}><span>Add a new User </span>
                                    <a href="/register"><button type="submit" className="btn btn-lg btn-primary ">Add a new User</button></a>
                                    </p>
                                </div>

                        </div>
                            {/*<LocalForm onSubmit={(values)=>this.handleSubmit(values)}>*/}
                            {/*    <h3>Log In</h3>*/}

                            {/*    <div className="form-group">*/}
                            {/*        <label>Username</label>*/}
                            {/*        <Control.text  model=".username" className="form-control" placeholder="Enter username" required="required" id="name" name="name" />*/}
                            {/*    </div>*/}

                            {/*    <div className="form-group">*/}
                            {/*        <label>Password</label>*/}
                            {/*        <Control.password model=".password" className="form-control" placeholder="Enter password" required="required" id="password" name="password" />*/}
                            {/*    </div>*/}


                            {/*    <br/>*/}
                            {/*    <button type="submit" className="btn btn-primary btn-block">Login</button>*/}
                            {/*    <br/>*/}
                            {/*    <p className="forgot-password text-right">*/}
                            {/*        New User ? <a href="/register"> Register here</a>*/}
                            {/*    </p>*/}
                            {/*</LocalForm>*/}
            </div>
            </div>
        );
    }
}
