import React, { Component } from "react";
import {Redirect} from "react-router-dom";
import store from 'store';
import './body.css'
import Home from "./home";
import {Button, Form} from "react-bootstrap";


class Deleteusers extends Component
{
    constructor(props)
    {
        super(props)
        this.state={
            users:[],
            checked_user:[]
        }
        this.onChange= this.onChange.bind(this);
        this.onSubmit= this.onSubmit.bind(this);
    }

    componentDidMount()
    {
        const apiUrl = 'http://localhost:8080/users';
        fetch(apiUrl)
            .then((response) => response.json())
            .then((data) => {this.setState({users:data})})
            .catch(err => console.log(err))
    }

    onChange(event)
    {
        const target = event.target;
        var value = target.value;
        console.log(value)

        var i =0;
        if(target.checked){
            this.state.checked_user[value] = value;
            console.log(this.state.checked_user)
        }else{
            this.state.checked_user.splice(value, 1);
            console.log(this.state.checked_user)
        }

    }

    onSubmit(event)
    {
        event.preventDefault()
        console.log(this.state.checked_user)
    }
    render() {
        const {users}=this.state;
        return (
            <div>

                <br/><br/><br/><br/>

            <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5">

            <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <h1 >Delete Users</h1>
                        <br/>
                        <label>Choose Users to Delete</label>

                        <Form.Group id="formGridCheckbox">
                            {users.map(item => (
                            <Form.Check type="checkbox" label={item.Username} value={item.Username} onClick={this.onChange}/>
                                ))}
                        </Form.Group>


                    </div>
                    <button type="submit" className="btn btn-primary btn-block">Delete Users</button>
                </form>

            </div>
            </div>
        );
    }
}

export default Deleteusers
