import React, { Component } from "react";

import {Button, Form ,Col ,Row} from 'react-bootstrap';
import Home from "./home";
import {Redirect} from "react-router";
class Credit extends Component
{
    constructor(props) {
        super(props);

        this.state={
            u_id:props.id,
            username:props.username,
            loggedIn:props.loggedIn,
            data:props.data,
            walletName:'',
            amount:'',
            home:false
        }

        this.onValueChange = this.onValueChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.goBack=this.goBack.bind(this);
    }

    componentDidMount() {
        const username = this.state.username
        const value = {username}
        console.log('Current State is: ' + JSON.stringify(value));

        const api = '/login';
        const request = {
            method: 'POST',
            headers: {'Accept': 'application/json', 'Content-Type': 'application/json'},
            body: JSON.stringify(value)
        };

        fetch(api, request)
            .then((response) => (response.json()))
            .then(res => this.setState({data: res}))
            .then((data) => console.log('logged in'))
            .then(this.setState({loggedIn: true}))
            .then(localStorage.setItem("token","abcd"))
            .catch(err => console.log(err))



    }

    onValueChange(e)
    {
        this.setState({
            [e.target.name]: e.target.type === 'text' ? parseInt(e.target.value) : e.target.value

        });
    }

    handleSubmit(e)
    {
        e.preventDefault();
        const {walletName , amount} = this.state;
        const value= {walletName,amount}
        console.log(JSON.stringify(value));


        const apiUrl = '/credit/'+this.state.u_id;
        const requestOptions = {
            method: 'POST',
            headers: { 'Accept':'application/json','Content-Type': 'application/json' },
            body: JSON.stringify(value)
        };

        fetch(apiUrl,requestOptions)
            .then(response=> response.json)
            .then(data=>console.log(data))
            .then(alert("money added"))
            .then(this.setState({walletName:'',
                amount:''}))
            .catch(err=>console.log(err))

        e.target.reset();
        this.componentDidMount()

    }

    goBack(e)
    {
        e.preventDefault()
        this.setState({home:true})

    }
    render() {

        if(this.state.home)
        {
            console.log(this.props.username , this.props.loggedIn  ,this.props.data)
            return <Home username={this.props.username} loggedIn={this.props.loggedIn} data={this.props.data} />
        }
        let i=0;
        const data=this.state.data;
        console.log(data)
        return (

            <div  className="page-holder bg-cover " style={{backgroundColor: '#c2eaba',
                backgroundImage: 'linear-gradient(319deg, #c2eaba 0%, #c5f4e0 37%, #efc2cf 100%)'}}>
                {console.log(data)}
                <br/><br/><br/><br/>
                <div className="card box_shw2 border-0 px-3 rounded-2 mb-3 w_500 py-4 mx-auto mt-5" >

                    <form onSubmit={this.handleSubmit}>
                        <h3>Add Money</h3>
                        <hr/>
                        <div className="form-group">

                            <label>Choose Wallet</label>

                            <fieldset >
                                <table width="400"  cellPadding="30" cellSpacing="20">
                                    <tr><td>Wallets</td><td>Amount</td><td>Status</td></tr>
                                {data.map((d)=>{
                                    return(
                                    <div key={i++}>

                                        <tr><td>
                                                    <input
                                                    type="radio"
                                                    label= {d.id}
                                                    name="walletName"
                                                    required="required"
                                                    value={d.wallet_name}
                                                    onChange={this.onValueChange}
                                                />{d.wallet_name}</td><td>{d.wallet_balance}</td><td>{d.is_active}</td>


                                        </tr>

                                    </div>
                                    );
                                })}
                                </table>
                            </fieldset>

                            <label>Amount</label>
                            <input type="text" pattern="[0-9]*" className="form-control" placeholder="Enter amount" required="required"  name="amount" value={this.state.amount} onChange={this.onValueChange} />
                        </div>

                        <br/>
                        <button type="submit" className="btn btn-primary btn-block">Add Money</button>
                        <br/>
                    </form>
                        <p className=" text-right">
                            <Button onClick={this.goBack}>Go Back</Button>
                        </p>

                </div>
            </div>
        );
    }

}
export default Credit;
